/* CSS: ipb_common*/
/************************************************************************/
/* IP.Board 3 CSS - By Rikki Tissier - (c)2008 Invision Power Services 	*/
/************************************************************************/
/* ipb_common.css														*/
/************************************************************************/


/************************************************************************/
/* LIGHTBOX STYLES */

#lightbox{	position: absolute;	left: 0; width: 100%; z-index: 16000 !important; text-align: center; line-height: 0;}
#lightbox img{ width: auto; height: auto;}
#lightbox a img{ border: none; }
#outerImageContainer{ position: relative; background-color: #fff; width: 250px; height: 250px; margin: 0 auto; }
#imageContainer{ padding: 10px; }
#loading{ position: absolute; top: 40%; left: 0%; height: 25%; width: 100%; text-align: center; line-height: 0; }
#hoverNav{ position: absolute; top: 0; left: 0; height: 100%; width: 100%; z-index: 10; }
#imageContainer>#hoverNav{ left: 0;}
#hoverNav a{ outline: none;}
#prevLink, #nextLink{ width: 49%; height: 100%; background-image: url(https://openflow.stanford.edu/forums/public/style_images/master/spacer.gif); /* Trick IE into showing hover */ display: block; }
#prevLink { left: 0; float: left;}
#nextLink { right: 0; float: right;}
#prevLink:hover, #prevLink:visited:hover { background: url(https://openflow.stanford.edu/forums/public/style_images/master/lightbox/prevlabel.gif) left 15% no-repeat; }
#nextLink:hover, #nextLink:visited:hover { background: url(https://openflow.stanford.edu/forums/public/style_images/master/lightbox/nextlabel.gif) right 15% no-repeat; }
#imageDataContainer{ font: 10px Verdana, Helvetica, sans-serif; background-color: #fff; margin: 0 auto; line-height: 1.4em; overflow: auto; width: 100%	; }
#imageData{	padding:0 10px; color: #666; }
#imageData #imageDetails{ width: 70%; float: left; text-align: left; }	
#imageData #caption{ font-weight: bold;	}
#imageData #numberDisplay{ display: block; clear: left; padding-bottom: 1.0em;	}			
#imageData #bottomNavClose{ width: 66px; float: right;  padding-bottom: 0.7em; outline: none;}
#overlay{ position: fixed; top: 0; left: 0; z-index: 15000 !important; width: 100%; height: 500px; background-color: #000; }

/************************************************************************/
/*  BBCODE STYLES */
/* 	NOTE: These selectors style bbcodes throughout IPB. It is recommended that you DO NOT change these 
	styles if you are creating a skin since it may interfere with user expectation
	of what certain BBCodes look like (quote boxes are an exception to this). */

strong.bbc				{	font-weight: bold !important; }
em.bbc 					{	font-style: italic !important; }
span.bbc_underline 		{ 	text-decoration: underline !important; }
acronym.bbc 			{ 	border-bottom: 1px dotted #000; }
span.bbc_center, div.bbc_center	{	text-align: center; display: block; }
span.bbc_left, div.bbc_left		{	text-align: left; display: block; }
span.bbc_right , div.bbc_right	{	text-align: right; display: block; }
div.bbc_indent 			{	margin-left: 50px; }
del.bbc 				{	text-decoration: line-through !important; }
ul.bbc 					{	list-style: disc outside; margin-left: 30px; }
	ul.bbc ul.bbc 			{	list-style-type: circle; }
		ul.bbc ul.bbc ul.bbc 	{	list-style-type: square; }
ul.bbcol.decimal 				{ margin-left: 30px; list-style-type: decimal; }
	ul.bbcol.lower-alpha		{ margin-left: 30px; list-style-type: lower-alpha; }
	ul.bbcol.upper-alpha		{ margin-left: 30px; list-style-type: upper-alpha; }
	ul.bbcol.lower-roman		{ margin-left: 30px; list-style-type: lower-roman; }
	ul.bbcol.upper-roman		{ margin-left: 30px; list-style-type: upper-roman; }
hr.bbc 					{ 	display: block; border-top: 2px solid #777; }
div.bbc_spoiler 		{	 }
div.bbc_spoiler span.spoiler_title	{ 	font-weight: bold; }
div.bbc_spoiler_wrapper	{ 	border: 1px inset #777; padding: 4px; }
div.bbc_spoiler_content	{ 	 }
input.bbc_spoiler_show	{ 	width: 45px; font-size: .7em; margin: 0px; padding: 0px; }
pre.prettyprint 		{ padding: 5px; background: #f8f8f8; border: 1px solid #c9c9c9; overflow: auto; margin-left: 10px; font-size: 11px; line-height: 140%; }
.bbc_member, .bbc_url, .bbc_email	{	color: inherit; }

/* Quote boxes */
p.citation {
	background: #d3e9dc url(https://openflow.stanford.edu/forums/public/style_images/master/citation_bg.png) no-repeat right;
	color: #2b3730;
	font-size: 0.9em;
	font-weight: bold;
	border-width: 1px 1px 0 3px;
	border-style: solid;
	border-color: #6fa987;
	padding: 5px 8px;
}

	div.blockquote {
		background: #f3f9f6;
		font-size: 0.9em;
		border-width: 1px 1px 1px 3px;
		border-style: solid;
		border-color: #6fa987;
		margin: 0px;
		margin-top: 0px;
	}

		div.blockquote p.citation {
			background-color: #e5d2e9;
			color: #413544;
			border-color: #ae7eb9;
		}

		div.blockquote div.blockquote {
			background: #f9f5fa;
			font-size: 1em;
			border-color: #ae7eb9;
			margin: -0px 15px 0px 15px;
		}

			div.blockquote div.blockquote p.citation {
				background-color: #e9e7d1;
				color: #474534;
				border-color: #b2ab72;
			}

			div.blockquote div.blockquote div.blockquote {
				background: #f9f8f3;
				border-color: #b2ab72;
			}

			div.blockquote p.citation {
				margin: 6px 15px 0 15px;
				font-size: 1em;
			}

div.quote {
	padding: 12px;
} 

div.quote p.blockquote {
	margin-left: -12px;
}

cite {
	display: block;
	font-weight: bold;
}

	blockquote cite {
		font-size: 1em;
	}
