/***************************************************************/
/* IP.Board 3 Editor CSS                                       */
/* ___________________________________________________________ */
/* By Rikki Tissier                                            */
/* (c)2008 Invision Power Services                             */
/***************************************************************/
/* ipb_editor.css - Styles for the editor (colors in main css) */
/***************************************************************/

#ipboard_body .input_rte {
	width: 100%;
	height: 250px;
}

	#ipboard_body .ips_editor.lite_edition .input_rte {
		height: 60px;
	}

	#ipboard_body .ips_editor .input_rte.unloaded_editor {
		font-family: arial, verdana, tahoma, sans-serif;
		font-style: italic;
		font-size: 11px;
		color: #b4b4b4;
		padding: 5px 0px;
		text-indent: 5px;
	}

#ipboard_body .ips_editor {
	/*background-color: #d5dde5;*/
	border: 2px solid #d5dde5;
	margin: 8px;
	margin-right: 0px;
	/*padding: 6px;*/
	position: relative;
	line-height: 100% !important;
}

	#ipboard_body .ips_editor.with_sidebar .controls,
	#ipboard_body .ips_editor.with_sidebar .editor{
		margin-right: 200px;
	}

	#ipboard_body .ips_editor .controls {
		position: relative;
	}

	/* Sidebar go bye-bye-bye (like Keith) */
	#ipboard_body .ips_editor.with_sidebar .sidebar {
		position: absolute;
		top: 0px;
		right: 1px;
		width: 195px;
		bottom: 1px;
		margin: 0;
		border-width: 1px;
		border-style: solid;
		border-color: #f6f8fa #d5dde5 #d5dde5 #f6f8fa;
	}

		#ipboard_body .ips_editor.with_sidebar .sidebar h4 {
			background-color: #e4ebf2;
			height: 25px;
			color: #1d3652;
			font-size: 10px;
			font-weight: bold;
		}

			#ipboard_body .ips_editor.with_sidebar .sidebar h4 span {
				padding: 6px 0 0 6px;
				display: block;
			}

			#ipboard_body .ips_editor.with_sidebar .sidebar h4 img {
				float: right;
				margin: 6px 6px 0 0;
				cursor: pointer;
			}

		#ipboard_body .ips_editor.with_sidebar .sidebar .emoticon_holder {
			width: 100%;
			height: 93%;
			overflow: auto;
			position: absolute;
			bottom: 25px;
			top: 25px;
		}

		#ipboard_body .ips_editor.with_sidebar .sidebar .emoticon_holder  td {
			padding: 5px 0;
		}

		#ipboard_body .show_all_emoticons {
			bottom: 0px;
			position: absolute;
			width: 100%;
			text-align: center;
			background: #e4ebf2;
			height: 25px;
		}

			#ipboard_body .ips_editor.with_sidebar .sidebar .emoticon_holder.no_bar {
				bottom: 0px;
			}

	#ipboard_body .ips_editor .toolbar {
		height: 30px;
		background: #e4ebf2 url(https://openflow.stanford.edu/forums/public/style_images/master/gradient_bg.png) repeat-x left 50%;
		border-width: 1px;
		border-style: solid;
		border-color: #f6f8fa #d5dde5 #d5dde5 #f6f8fa;
	}

		#ipboard_body .ips_editor .toolbar li {
			float: left;
			padding: 3px;
		}

			#ipboard_body .ips_editor .toolbar li.sep {
				padding-right: 4px;
				border-right: 1px solid #d5dde5;
				margin-right: 4px;
			}

			#ipboard_body .ips_editor .toolbar li.left {
				float: left;
			}

			#ipboard_body .ips_editor .toolbar li.right {
				float: right;
			}

		#ipboard_body .ips_editor .toolbar li span {
			display: block;
			padding: 3px;
		}

	#ipboard_body .ips_editor ul.ipbmenu_content,
	#ipboard_body .ips_editor ul.ipbmenu_content li {
		display: block;
		float: none;
		background-color: #fff;
	}

#ipboard_body .ips_editor .toolbar li .rte_control.rte_menu {
	font-size: 11px;
	height: 14px;
	border: 1px solid #aeb9c5;
	margin-top: 1px;
	padding: 4px 15px 2px 7px;
	background-color: #fff;
	background-image: url(https://openflow.stanford.edu/forums/public/style_images/master/rte_icons/rte_arrow.png);
	background-repeat: no-repeat;
	background-position: right center;
}

#ipboard_body .rte_title {
	background-color: #d5dde5;
